mapper.select:
SELECT  L.ODD_CITY
, L.ODD_DISTRICT
, L.ODD_DONG
, L.ODD_X
, L.ODD_Y
, L.ODD_FULLNAME
FROM ODD_LOCATION L


        AND L.ODD_DISTRICT = #{district}


        AND L.ODD_DONG  =  #{dong}


        AND L.ODD_FULLNAME LIKE CONCAT('%',#{fullName},'%') ;

mapper.insert:
INSERT INTO ODD_LOCATION (
                          ODD_CITY
                          , ODD_DISTRICT
                          , ODD_DONG
                          , ODD_X
                          , ODD_Y
                          , del_yn
                          , ODD_FULLNAME
                          ) VALUES

(
    #{item.city}
    , #{item.district}
    , #{item.dong}
    , #{item.x}
    , #{item.y}
    , 'Y'
    , #{item.fullName}
)
 ON DUPLICATE KEY  UPDATE DEL_YN = 'N';
